import React, { Component } from 'react'
import Swiper from 'react-id-swiper'

export class BlogBanner extends Component {
    render() {
        const params = {
            pagination: {
              el: '.swiper-pagination',
              type: 'bullets',
              clickable: true
            },
            navigation: {
              nextEl: '.swiper-button-next',
              prevEl: '.swiper-button-prev'
            },
            loop: true,
            autoplay: {
                delay: 3000
            },
            effect: 'flip',
            grabCursor: true,
            spaceBetween: 30
        }
        return (
            <div>
                <Swiper {...params}>
                            <div>
                                <div className="review-wrapper">
                                    <div className="img-wrapper">
                                        <img src="/images/clients/client-welcom-hotel-Dwarka.png" alt="SEO agency in Delhi"/>
                                    </div>
                                    <p className="quote">The team provided exceptional dedication to us and was very responsive while answering the questions we had. Their integrity and honesty stands out, and everything is upfront. With their creative planning, the posts helped us establish a strong social media presence.  The team did a great job and we are pleased with what they accomplished. </p>
                                    <h3 className="text-center mt-5">Prateek Suri</h3>
                                    <p className="text-center">Sales and Marketing Manager</p>
                                </div>
                            </div>
                            <div>
                                <div className="review-wrapper">
                                    <div className="img-wrapper">
                                        <img src="/images/clients/client-shadanga.png" alt="digital marketing agency in Delhi"/>
                                    </div>
                                    <p className="quote">Impressed with the team’s communication and quality of work. AmitKK is the best website development company in Delhi. Our website development project gets treated as their own, and the team has showed exceptional technical skills and intelligence. We are very happy and satisfied with the results obtained!</p>
                                    <h3 className="text-center mt-5">Dr. Jacob</h3>
                                    <p className="text-center">Owner - Shadanga</p>
                                </div>
                            </div>
                            <div>
                                <div className="review-wrapper">
                                    <div className="img-wrapper">
                                        <img src="/images/clients/client-csel.png" alt="website development agency in Delhi"/>
                                    </div>
                                    <p className="quote">The team’s collaborative approach, their personality and passion made it easy to work with Amit. Every team member is enthusiastic and genuinely cares about the success of our digital marketing project. Overall, the web development efforts have met my needs and have been effective. I highly recommend AmitKK for website and app development.</p>
                                    <h3 className="text-center mt-5">Vrinda Loiwal</h3>
                                    <p className="text-center">Owner - CSEL</p>
                                </div>
                            </div>
                            <div>
                                <div className="review-wrapper">
                                    <div className="img-wrapper">
                                        <img src="/images/clients/client-shake-busters.png" alt="graphics designing agency in Delhi"/>
                                    </div>
                                    <p className="quote">We recommended the services of Amit to every company that wishes to take a big leap with website development and digital marketing. The success of the project has exceeded our expectations. It was a refreshing experience to work with a team that cares about your growth and results. Teir design concepts are really cool.</p>
                                    <h3 className="text-center mt-5">Shrom Budhraja</h3>
                                    <p className="text-center">Owner - Shake Buster</p>
                                </div>
                            </div>
                            <div>
                                <div className="review-wrapper">
                                    <div className="img-wrapper">
                                        <img src="/images/clients/client-whitesmile.png" alt="social media marketing agency in Delhi"/>
                                    </div>
                                    <p className="quote">The team understands what a good website looks like. We can hear in Amit’s voice about the pride he takes in over-delivering and seeing the success of the website development projects. I was impressed with his knowledge, and his ability to explain how a specific strategy can work wonders for your business.</p>
                                    <h3 className="text-center mt-5">Dr. Ankur </h3>
                                    <p className="text-center">Owner - Whitesmile</p>
                                </div>
                            </div>
                        </Swiper>
            </div>
        )
    }
}

export default BlogBanner




