import React from 'react'
import Swiper from 'react-id-swiper'

function BlogBanner(props) {
    console.log('props.blogs', props.blogs)
    const params = {
        pagination: {
          el: '.swiper-pagination',
          type: 'bullets',
          clickable: true
        },
        navigation: { 
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev'
        },
        loop: true,
        autoplay: {
            delay: 3000
        },
        effect: 'flip', 
        grabCursor: true,
        spaceBetween: 30
    }
    return (
        <Swiper {...params}>
                {props.blogs.map((i)=>{
                    <div>
                        <img src="/images/clients/client-welcom-hotel-Dwarka.png" alt="SEO agency in Delhi"/>
                        <div className="carousel-caption">
                            <a href={"/blog/" + i.slug}><h2>{i.title}</h2></a>
                            <a href={"/blog/" + i.slug} className="amitBtn">Read More</a>
                        </div>
                    </div>
                })}
            </Swiper>
    )
}

export default BlogBanner




