import React from 'react'

function BlogList(props) {
    console.log('props', props)
    return (
        <>
        { this.props.blogs ?
                    <>
            { props.blogs.map((i)=>{ 
                // let alt = i.cover_img.replace('.jpg', '').replace(/_/g, ' ').replace(/-/g, ' ')
            return(
                <div className="col-sm-4 " key={i.id}>
                    <div className="card">
                        <a href={"/" + i.url}>
                            <div><img src={i.coverImg }/></div>
                            <div className="name">
                                <h2>{i.title}</h2>
                                <p>Read More </p>
                            </div>
                        </a>
                    </div>
                </div>
            )})}
        </>
        : null }
        </>
    )
}

export default BlogList
